static const uint8_t (*animation_frames[])[128] = {
    anim_frame_0,
    anim_frame_1,
    anim_frame_2,
    anim_frame_3,
    anim_frame_4,
    anim_frame_5,
    anim_frame_6
};

void CUserInterface::ShowCuteFaceAnimation(CLCD& LCD)
{
    CMT32Pi::s_pThis->m_bAbortSleepAnimation = false;

    const u32 startTime = CTimer::GetClockTicks();
    const u32 totalDuration = Utility::MillisToTicks(600 * 60 * 1000); // 600 Minutes
    const u32 frameDelay = Utility::MillisToTicks(100); // 100ms per frame
    const size_t frameCount = 7;

    while (!CMT32Pi::s_pThis->m_bAbortSleepAnimation &&
           (CTimer::GetClockTicks() - startTime) < totalDuration)
    {
        u32 elapsed = CTimer::GetClockTicks() - startTime;
        size_t frame = (elapsed / frameDelay) % frameCount;

        LCD.Clear(false);
        for (u8 y = 0; y < 32; ++y)
        {
            for (u8 x = 0; x < 128; ++x)
            {
                if (animation_frames[frame][y][x])
                    LCD.SetPixel(x, y);
                else
                    LCD.ClearPixel(x, y);
            }
        }

        LCD.Flip();
        CTimer::SimpleMsDelay(10); // small delay to avoid tight loop
        CScheduler::Get()->Yield();
    }
}
